<?phpfunction config(){       // User Authentication for Database 			$con=mysql_connect('localhost','username','');	//removed Username & Password for security.			 	if (!$con){		die('Could not connect: ' . mysql_error());	}	//Selecting Database	$db_selected=mysql_select_db('dbname',$con); //removed DBname for security.			//returning Connection Object	return $db_selected;}?>